
var stamp = new Date();
var orderID = stamp.getDay()+""+stamp.getHours()+""+stamp.getMilliseconds();
var dataConfig = "judul";

//================== GANTI DENGAN NILAI INPUT SENDIRI =======================
// ***************** 	 MENGAMBIL NILAI ELEMENT      ***********************

var nama = $("#nama_input");
var alamat = $("#alamat_input");
var email = $("#email_input");

//==================++++++++++++++++++++++++++++++++++=======================

var setData = dataConfig + "/" + orderID;
const namaData = "/" + setData;
const db = firebase.database();
const dbRef = db.ref("/users");
const skopRef = dbRef.child(namaData);
$("#tulisData").click(e=> {
	// GANTI STRUKTUR DATABASE
  skopRef.set(
      {
      a : nama.val(),
      b : alamat.val(),
      c : email.val()
      }
  );
  console.log(orderID+ " Berhasil!");
});


$(document).ready(function(){

	dbRef.child("judul").on("value", persiapan => {
		$("#data").html("");
		persiapan.forEach(baca =>{
			
			const kunci = baca.key;
			const nilai = baca.val();
			//kode eksekusi
			console.log(nilai.a);
			var tableN = "<tr><td data-id="+kunci+">"+nilai.a+"</td><td>"+nilai.b+"</td><td>"+nilai.c+"</td></tr>";

			$("#data").append(tableN);


		});
	});
});